var receivedData = msg.payload;
try {
    var jsonData = JSON.parse(receivedData);

    // Przypisanie wartości do kontekstu
    context.set('name', jsonData.name);
    context.set('timeWatering', jsonData.timeWatering);
    context.set('temperature', jsonData.temperature);
    context.set('pressure', jsonData.pressure);
    context.set('humidity', jsonData.humidity);
    context.set('soilMoisture', jsonData.moilSoilture);

    // Sprawdź, czy wszystkie dane są dostępne
    var name = context.get('name');
    var timeWatering = context.get('timeWatering');
    var temperature = context.get('temperature');
    var pressure = context.get('pressure');
    var humidity = context.get('humidity');
    var soilMoisture = context.get('soilMoisture');

    if (temperature !== undefined && pressure !== undefined && humidity !== undefined && soilMoisture !== undefined && name !== undefined &&   timeWatering !== undefined) {
        // Wykonaj operację zapisu do bazy danych za pomocą węzła bazy danych w Node-RED
        var date = getCurrentDateTime();
        var query = 'INSERT INTO plant_data(moilSoilture, temperature, humidity, pressure,date,name,timeWatering) VALUES (?,?,?,?,?,?,?)';
        var params = [soilMoisture, temperature, humidity, pressure, date, name, timeWatering ];

        msg.topic = query;
        msg.payload = params;

        // Wyzeruj dane w kontekście węzła
        context.set('name', undefined);
        context.set('timeWatering', undefined);
        context.set('temperature', undefined);
        context.set('pressure', undefined);
        context.set('humidity', undefined);
        context.set('soilMoisture', undefined);

        return msg;
    }
} catch (error) {
    node.error("Błąd w parsowaniu JSONa: " + error.message);
}


function getCurrentDateTime() {
    var now = new Date();
    var year = now.getFullYear();
    var month = padNumber(now.getMonth() + 1);
    var day = padNumber(now.getDate());
    var hours = padNumber(now.getHours());
    var minutes = padNumber(now.getMinutes());
    var seconds = padNumber(now.getSeconds());

    return year + '-' + month + '-' + day + ' ' + hours + ':' + minutes + ':' + seconds;
}

function padNumber(num) {
    return (num < 10 ? '0' : '') + num;
}
