var name = data.name;
var timeWatering = data.timeWatering;
var moilSoilture = data.moilSoilture;
var temperature = data.temperature;
var humidity = data.humidity;
var pressure = data.pressure;


var extractedData = {
    "Name": name,
    "Time Watering": timeWatering,
    "Soil Moisture": moilSoilture,
    "Temperature": temperature,
    "Humidity": humidity,
    "Pressure": pressure
