context.set('name', undefined);
context.set('timeWatering', undefined);
context.set('temperature', undefined);
context.set('pressure', undefined);
context.set('humidity', undefined);
context.set('moilSoilture', undefined);

var date = getCurrentDateTime();
var query = 'INSERT INTO plant_data(moilSoilture, temperature, humidity, pressure, date, timeWatering, name) VALUES (?,?,?,?,?,?,?)';
var params = [moilSoilture, temperature, humidity, pressure, date, timeWatering, name];

msg.topic = query;
msg.payload = params;

return msg;

function getCurrentDateTime() {
    var now = new Date();
    var year = now.getFullYear();
    var month = padNumber(now.getMonth() + 1);
    var day = padNumber(now.getDate());
    var hours = padNumber(now.getHours());
    var minutes = padNumber(now.getMinutes());
    var seconds = padNumber(now.getSeconds());

    return year + '-' + month + '-' + day + ' ' + hours + ':' + minutes + ':' + seconds;
}

function padNumber(num) {
    return (num < 10 ? '0' : '') + num;
}
